import React, { useState } from "react";
import "./App.css";
import ProductCard from "./ProductCard";

export default function App() {
  const [cart, setCart] = useState(0);
  const [showToast, setShowToast] = useState(false);

  const handleAdd = () => {
    setCart(cart + 1);
    setShowToast(true);

    setTimeout(() => {
      setShowToast(false);
    }, 1500);
  };

  return (
    <div className="container">
      <nav className="navbar">
        <h1>BALE' Store</h1>
        <div className="cart-badge">ðŸ›’ {cart}</div>
      </nav>
      {showToast && (
        <div className="toast">
          âœ… Produk berhasil ditambahkan
        </div>
      )}

      <section className="produk">
        <ProductCard
          image="data:image/jpeg;base64,"
          title="Indomilk Coklat"
          className="rolate-logo"
          desc="Susu coklat lezat"
          price="Rp 6.000"
          onAdd={handleAdd}
        />
      </section>

        <section className="produk">
        <ProductCard
          image="data:image/jpeg;base64,"
          title="Indomilk Strawberry"
          className="rolate-logo"
          desc="Susu strawberry lezat"
          price="Rp 6.000"
          onAdd={handleAdd}
        />
      </section>

        <section className="produk">
        <ProductCard
          image="data:image/jpeg;base64,"
          title="Indomilk Melon"
          className="rolate-logo"
          desc="Susu melon lezat"
          price="Rp 6.000"
          onAdd={handleAdd}
        />
      </section>

        <section className="produk">
        <ProductCard
          image="data:image/jpeg;base64,"
          title="Indomilk Vanila"
          desc="Susu vanila lezat"
          className="rolate-logo"
          price="Rp 6.000"
          onAdd={handleAdd}
        />
      </section>
    </div>
  );
}
